import React, { useState } from 'react'

function Box()
{
    const [text,setText]= useState("");

    function toggleText()
    {
        if(text==="")
        {
            setText("meepo.png");
        }
        else
        {
            setText("");
        }
    }

return <div className="box" onClick={toggleText}><img src={require(`./icons/meepo.png`)}/></div>;
}

export default Box;
