import React, { useEffect, useState } from 'react'
import './style.css'
import Box from './Box'

function Game()
{
    const SIZE=3*3;
    const ROW_LENGTH=3;
    const INTERVAL=1000;
    const [score, setScore]= useState(0);

    let imgConfig=new Array(SIZE).fill(true);
    //imgConfig[0]=true;

    console.log(imgConfig);

    function incScore()
    {
        setScore(score=>score+1);
    }

    const meepImg=<img src={require(`./icons/meepo.png`)} alt="meepo" />;

    const [img,setImg]= useState(meepImg);

    function toggleImg()
    {
        //console.log("called");
         //setImg((img==null?meepImg:null));

         const rand=Math.floor(Math.random() * (SIZE)) + 0; 
         imgConfig.fill(false);
         imgConfig[rand]=true;
         //setImg(meepImg);

         console.log(imgConfig);
    }

    
    useEffect(() => 
    {
        const interval = setInterval(toggleImg, INTERVAL);
        return () => {
                         clearInterval(interval);
                    };
    }, [img]);



    function getTableBody()
    {
        let count=0;
        let body=<html></html>;

        /* for(let i=count;i<SIZE/ROW_LENGTH;i++)
        {
            body+=
            imgConfig.map((value,index)=>
            {
                return <div className="row">
                <Box id={index} currentState={()=>
                {    
                    imgConfig[index]=!value;
                    return value?null:meepImg;
                }} toggle={incScore}/>
                </div>;
            });
        } */

      
        return body;
    }

    console.log(getTableBody());


    let toRetun=<div id="game">
    <h1>Whack-a-Meepo!</h1>
    
    
    <div className="row">
        <Box id={0} currentState={imgConfig[0]?img:null} toggle={incScore}/>
        <Box id={1} currentState={imgConfig[1]?img:null} toggle={incScore}/>
        <Box id={2} currentState={imgConfig[2]?img:null} toggle={incScore}/>
    </div>

    <div className="row">
        <Box id={3} currentState={imgConfig[3]?img:null} toggle={incScore}/>
        <Box id={4} currentState={imgConfig[4]?img:null} toggle={incScore}/>
        <Box id={5} currentState={imgConfig[5]?img:null} toggle={incScore}/>
    </div>

    <div className="row">
        <Box id={6} currentState={imgConfig[6]?img:null} toggle={incScore}/>
        <Box id={7} currentState={imgConfig[7]?img:null} toggle={incScore}/>
        <Box id={8} currentState={imgConfig[8]?img:null} toggle={incScore}/>
    </div>
    <h2>Score: {score}</h2>
</div>;

    return toRetun;
}

export default Game;
