import React, { useState } from 'react'
import setScore from './index.js'

function Box()
{
    const incrementScore=()=>{}

    const meepImg=<img src={require(`./icons/meepo.png`)} onMouseDown={e=>
     {   
      
            setScore(1);
        
    }
    }  />;
    const [img,setImg]= useState();

    function toggle(e)
    {
        
        e.stopPropagation();

        if(img==="" )
        {
            setImg(meepImg);
        }
        else
        {
            setImg("");
        }
        
    }

return <div className="box" onMouseDown={toggle}>
    {img}
    </div>;
}

export default Box;
