import React, { useState } from 'react'
import incrementScore from './index.js'

function Box(props)
{
    const meepImg=<img src={require(`./icons/meepo.png`)} alt="meepo" onMouseDown={props.score++}  />;


    const [img,setImg]= useState("");

    function toggle(e)
    {
        e.stopPropagation();

        if(img==="" )
        {
            setImg(meepImg);
        }
        else
        {
            setImg("");
        }
        
    }

    return <div className="box" onMouseDown={toggle}>
        {img}
        </div>;
}

export default Box;
