import React, { useState } from 'react'
import incrementScore from './index.js'

function Box(props)
{
    const[meep, setMeep]=useState(props.currentState);

    function toggleMeep(){
        console.log("meep!");
        if(meep==="")
        {
            setMeep(props.currentState);
        }
        else
        {
            setMeep("");
            
        }
        props.toggle(meep);
    }

    return <div className="box" onMouseDown={toggleMeep}>
        {meep}
        </div>;
}

export default Box;
