import React, {useState,useEffect, useCallback} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from "axios"
import {useDropzone} from 'react-dropzone'
import Game from './components/Game/Game'




//functional component 
const UserProfiles = () => 
{
  const [profiles, setUserProfiles]= useState([]);

    const fetchUserProfiles=() =>
    {
        axios.get("http://localhost:8080/api/v1/user-profile").then(res=>
        {
          console.log(res.data); 
          setUserProfiles(res.data);
        });
    }

    useEffect(()=>
    {
        fetchUserProfiles();
    }, []);

    return profiles.map((UserProfile,index)=>{

      return (
        <div key = {index} className="ProfileBox">
          {UserProfile.userProfileID ? <img src={`http://localhost:8080/api/v1/user-profile/${UserProfile.userProfileID}/image/download`} />:null}
          <br/><br/>
          <h2>{UserProfile.username}</h2>
          <p>Score: {UserProfile.score}</p>
          <br/>
        </div>
      ) // <Dropzone userProfileID={UserProfile.userProfileID}/>
    })
}

function Dropzone({userProfileID}) {
  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];

    console.log(file);

    let formData = new FormData();
    formData.append("file",file);

    axios.post(
      `http://localhost:8080/api/v1/user-profile/${userProfileID}/image/upload`, formData, {headers: {'Content-Type': 'multipart/form-data'}      }
    ).then(resp=>{console.log(resp); window.location.reload(true);}).catch(err=>console.log(err));

  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  return (
    <div {...getRootProps()}>
      <input {...getInputProps()} />
      {
        isDragActive ?
          <p>Drop the image here ...</p> :
          <p>Drag 'n' drop profile image, or click to select</p>
      }
    </div>
  )
}

function saveScore(score, name, file)
{
  let formData = new FormData();

  console.log(formData);
  formData.append("file",file);
  formData.append("name",name);  
  formData.append("score",score);
  console.log("FILE: "+file);
  let returnValue="";

  return axios.get("http://localhost:8080/api/v1/user-profile").then(res=>
  {
    let profiles=[];
    profiles=res.data;
    console.log(profiles);

    let found=false;

      profiles.map((UserProfile,index)=>{

      if(UserProfile.username === name)
      {
        found=true;
      }

    });


    if(!found)
    {

    axios.post(
      `http://localhost:8080/api/v1/user-profile/score/upload`, formData, {headers: {'Content-Type': 'multipart/form-data'} }
          ).then(resp=>{window.location.reload(true);}).catch(err=>console.log(err));

          returnValue= "";
    }
    else
    {
      returnValue= "User already exists, try another one";
    }

    return returnValue;
  }).catch(err=>console.log(err));
}

function App() {
  return (
    <div className="App">
      <h1>Whack-a-Meepo!</h1>
      <Game saveScore={saveScore}/>
      <h1>Leaderboards</h1><br/><br/>
      <UserProfiles/>
      </div>
  );
}

export default App;
