import React, { useState } from 'react'

function Box()
{
    const [text,setText]= useState('');

    function toggleText()
    {
        if(text==='')
        {
            setText("./src/icons/meepo.png");
        }
        else
        {
            setText('');
        }
    }

return <div className="box" onClick={toggleText}><img alt="meepo" src={text}></img></div>;
}

export default Box;
