import React, { useEffect, useState } from 'react'
import './style.css'
import Box from './Box'

function Game()
{
    const size=3;
    const [score, setScore]= useState(0);

    function incScore()
    {
        setScore(score=>score+1);
    }

    return <div id="game">
        <h1>Whack-a-Meepo!</h1>
        <div className="row">
            <Box id={0} score={incScore}/>
            <Box id={1} score={incScore}/>
            <Box id={2} score={incScore}/>
        </div>

        <div className="row">
            <Box id={3} score={incScore}/>
            <Box id={4} score={incScore}/>
            <Box id={5} score={incScore}/>
        </div>

        <div className="row">
            <Box id={6} score={incScore}/>
            <Box id={7} score={incScore}/>
            <Box id={8} score={incScore}/>
        </div>
        <h2>Score: {score}</h2>
    </div>;
}

export default Game;
