import React, { useEffect, useState } from 'react'
import './style.css'
import Box from './Box'

function Game()
{
    const size=3;
    const [score, setScore]= useState(0);

    function incScore()
    {
        setScore(score=>score+1);
    }

    const meepImg=<img src={require(`./icons/meepo.png`)} alt="meepo" />;

    const [img,setImg]= useState("");

    function toggle(e)
    {
        e.stopPropagation();

        if(img==="" )
        {
            setImg(meepImg);
        }
        else
        {
            setImg("");
        }
        
    }

    return <div id="game">
        <h1>Whack-a-Meepo!</h1>
        <div className="row">
            <Box id={0} currentState={img} toggle={toggle}/>
            <Box id={1} currentState={img} toggle={toggle}/>
            <Box id={2} currentState={img} toggle={toggle}/>
        </div>

        <div className="row">
            <Box id={3} currentState={img} toggle={toggle}/>
            <Box id={4} currentState={img} toggle={toggle}/>
            <Box id={5} currentState={img} toggle={toggle}/>
        </div>

        <div className="row">
            <Box id={6} currentState={img} toggle={toggle}/>
            <Box id={7} currentState={img} toggle={toggle}/>
            <Box id={8} currentState={img} toggle={toggle}/>
        </div>
        <h2>Score: {score}</h2>
    </div>;
}

export default Game;
