import React, { useEffect, useState,useCallback } from 'react'
import './style.css'
import Box from './Box'
import { PropTypes } from 'react'
import {useDropzone} from 'react-dropzone'

function Game(props)
{
    const SIZE=3*3;
    const ROW_LENGTH=3;
    const INTERVAL=1000;

    const [name, setName] =useState("");
    const [score, setScore]= useState(0);

    const audio1= new Audio(require(`./sounds/attack1.mp3`));
    audio1.volume=0.1;

    let startingArr=new Array(SIZE).fill(false);
    startingArr[0]=true;

    const [arr, setArr]= useState(startingArr);

    const [error,setError] = useState("");

    function incScore()
    {
        setScore(score=>score+1);
        playAttackSound();
    }
    
    function playAttackSound()
    {
        audio1.play();
    }

    function setClicked()
    {
        incScore();
    }


    const meepImg=<img id ="meepImg" src={require(`./icons/meepo.png`)} alt="meepo" />;

    const [img,setImg]= useState(meepImg);

    function toggleImg()
    {
         const rand=Math.floor(Math.random() * (SIZE)) + 0; 
         let tempArr=new Array(SIZE).fill(false);
         tempArr[rand]=true;
         
         setArr(tempArr);
    }

    
    useEffect(() => 
    {
        const interval = setInterval(toggleImg, INTERVAL);
        return () => {
                         clearInterval(interval);
                    };
    }, [img]);



    function getTableBody()
    {
        let count=0;
        let body=<html></html>;

        /* for(let i=count;i<SIZE/ROW_LENGTH;i++)
        {
            body+=
            arr.map((value,index)=>
            {
                return <div className="row">
                <Box id={index} currentState={()=>
                {    
                    arr[index]=!value;
                    return value?null:meepImg;
                }} setClicked={setClicked}/>
                </div>;
            });
        } */
      
        return body;
    }

    function saveScore(e)
    {
        e.preventDefault();

        if(name.length<2)
        {
            setError("Name cannot be less than 2 characters");
            return;
        }
        else if(score==0)
        {
            setError("Try scoring something first...");
            return;
        }
        else
        {
            props.saveScore(score,name).then(error=>setError(error));
        }
    }

    function Dropzone() {
        const onDrop = useCallback(acceptedFiles => {
          const file = acceptedFiles[0];
          
          console.log(file);
          
          /*let formData = new FormData();
          formData.append("file",file);
      
          axios.post(
            `http://localhost:8080/api/v1/user-profile/${userProfileID}/image/upload`, formData, {headers: {'Content-Type': 'multipart/form-data'}      }
          ).then(resp=>{console.log(resp); window.location.reload(true);}).catch(err=>console.log(err));*/
      
        }, [])
        const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})
      
        return (
          <div {...getRootProps()}>
            <input {...getInputProps()} />
            {
              isDragActive ?
                <p>Drop the image here ...</p> :
                <p>Drag 'n' drop profile image, or click to select</p>
            }
          </div>
        )
      }

    let toRetun=<div id="game">
    <div className="border">
        <div className="row">
            <Box id={0} currentState={arr[0]?img:null} setClicked={setClicked}/>
            <Box id={1} currentState={arr[1]?img:null} setClicked={setClicked}/>
            <Box id={2} currentState={arr[2]?img:null} setClicked={setClicked}/>
        </div>

        <div className="row">
            <Box id={3} currentState={arr[3]?img:null} setClicked={setClicked}/>
            <Box id={4} currentState={arr[4]?img:null} setClicked={setClicked}/>
            <Box id={5} currentState={arr[5]?img:null} setClicked={setClicked}/>
        </div>

        <div className="row">
            <Box id={6} currentState={arr[6]?img:null} setClicked={setClicked}/>
            <Box id={7} currentState={arr[7]?img:null} setClicked={setClicked}/>
            <Box id={8} currentState={arr[8]?img:null} setClicked={setClicked}/>
        </div>
    </div>
    <h2>Score: {score}</h2><br/>
    <form  onSubmit={saveScore}>
          <label>Name: </label>
          <input type="text" id="fname" name="fname" onChange={(e)=>{setName(e.target.value)}}></input><br></br>
          <Dropzone/>
          <button type="submit" >Save Score</button>
          <p className="error">{error}</p>
    </form> 
</div>;

    return toRetun;
}

export default Game;
