import React, { useState } from 'react'

function Box()
{
    const [img,setImg]= useState(<img src={require(`./icons/meepo.png`) }/>);

    function toggle(e)
    {
        e.preventDefault();
        
        if(img==="" )
        {
            setImg(<img src={require(`./icons/meepo.png`) } onClick="console.log('click')"/>);
        }
        else
        {
            setImg("");
        }
        
        
    }

return <div className="box" onClick={toggle}>
    {img}
    </div>;
}

export default Box;
