import React, { useEffect, useState } from 'react'
import './style.css'
import Box from './Box'

function Game()
{
    const SIZE=3;
    const INTERVAL=1000;
    const [score, setScore]= useState(0);

    function incScore()
    {
        setScore(score=>score+1);
    }

    const meepImg=<img src={require(`./icons/meepo.png`)} alt="meepo" />;

    const [img,setImg]= useState(meepImg);

    function toggleImg()
    {
        //console.log("called");
         setImg((img==null?meepImg:null));
    }

    
  useEffect(() => {
    const interval = setInterval(toggleImg,INTERVAL);
    return () => {
      clearInterval(interval);
    };
  }, [img]);

    return <div id="game">
        <h1>Whack-a-Meepo!</h1>
        <div className="row">
            <Box id={0} currentState={img} toggle={incScore}/>
            <Box id={1} currentState={img} toggle={incScore}/>
            <Box id={2} currentState={img} toggle={incScore}/>
        </div>

        <div className="row">
            <Box id={3} currentState={img} toggle={incScore}/>
            <Box id={4} currentState={img} toggle={incScore}/>
            <Box id={5} currentState={img} toggle={incScore}/>
        </div>

        <div className="row">
            <Box id={6} currentState={img} toggle={incScore}/>
            <Box id={7} currentState={img} toggle={incScore}/>
            <Box id={8} currentState={img} toggle={incScore}/>
        </div>
        <h2>Score: {score}</h2>
    </div>;
}

export default Game;
