import React, { useState } from 'react'
import incrementScore from './index.js'

function Box(props)
{

    console.log(props.id);
    const[meep, setMeep]=useState(props.currentState);

    function toggleMeep()
    {
        console.log("meep!");
        if(meep!="")//has meepo img
        {
            setMeep(""); //it disappears (poof!)
            props.toggle(meep); //increment score in higher level compoenent 
        }
        
    }

    return <div className="box" onMouseDown={toggleMeep}>
        {meep}
        </div>;
}

export default Box;
