import React, { useEffect, useState } from 'react'
import './style.css'
import Box from './Box'

function Game()
{
    const [score,setScore]= useState(0);

    const meepImg=<img src={require(`./icons/meepo.png`)} alt="meepo" onMouseDown={console.log("score")}  />;


    const [img,setImg]= useState("");

    function toggle(e)
    {
        //e.stopPropagation();

        if(img==="" )
        {
            setImg(meepImg);
        }
        else
        {
            setImg("");
        }
        
    }

    return <div id="game">
        <h1>Whack-a-Meepo!</h1>
        <div className="row">
            <Box id={0} currentState={meepImg} toggle={toggle}/>
            <Box id={1} currentState={meepImg} toggle={toggle}/>
            <Box id={2} currentState={meepImg} toggle={toggle}/>
        </div>

        <div className="row">
            <Box id={3} currentState={meepImg} toggle={toggle}/>
            <Box id={4} currentState={meepImg} toggle={toggle}/>
            <Box id={5} currentState={meepImg} toggle={toggle}/>
        </div>

        <div className="row">
            <Box id={6} currentState={meepImg} toggle={toggle}/>
            <Box id={7} currentState={meepImg} toggle={toggle}/>
            <Box id={8} currentState={meepImg} toggle={toggle}/>
        </div>
        <h2>Score: {score}</h2>
    </div>;
}

export default Game;
